function formatBytes(bytes) {
	if (bytes === 0) return 0;
	var k = 1000,
	sizes = ['', 'K', 'M', 'G', 'T', 'P', 'E', 'Z', 'Y'],
	i = Math.floor(Math.log(bytes) / Math.log(k));
	return (bytes / Math.pow(k, i)).toFixed(i==0?0:2)+sizes[i];
}
function IsMatch(min, max, val){
	return min <= val && val <= max
}

